  if (window.addEventListener) {
    window.addEventListener( & #39;
    load & #39;, function () {
      object[attribute] = val;
    }, false);
  } else {
    window.attachEvent( & #39;
    onload & #39;, function () {
      object[attribute] = val;
    });
  }
}

